import React, { Component } from 'react'
import ReactTable from 'react-table'
import api from '../api'

import styled from 'styled-components'

import 'react-table/react-table.css'

const Wrapper = styled.div`
    padding: 0 40px 40px 40px;
`

class MembersList extends Component {
    constructor(props) {
        super(props)
        this.state = {
            members: [],
            columns: [],
            isLoading: false,
        }
    }

    componentDidMount = async () => {
        this.setState({ isLoading: true })

        await api.getAllMembers().then(members => {
            this.setState({
                members: members.data.data,
                isLoading: false,
            })
        })
    }

    render() {
        const { members, isLoading } = this.state
        console.log('TCL: MembersList -> render -> members', members)

        const columns = [
            {
                Header: 'ID',
                accessor: '_id',
                filterable: true,
            },
            {
                Header: 'Firstname',
                accessor: 'firstName',
                filterable: true,
            },
            {
                Header: 'Middlename',
                accessor: 'middleName',
                filterable: true,
            },
            {
                Header: 'Lastname',
                accessor: 'lastName',
                filterable: true,
            },
        ]

        let showTable = true
        if (!members.length) {
            showTable = false
        }

        return (
            <Wrapper>
                {showTable && (
                    <ReactTable
                        data={members}
                        columns={columns}
                        loading={isLoading}
                        defaultPageSize={10}
                        showPageSizeOptions={true}
                        minRows={0}
                    />
                )}
            </Wrapper>
        )
    }
}

export default MembersList
