import React, { Component } from 'react'
import ReactTable from 'react-table-6'
import api from '../api'

import styled from 'styled-components'

import 'react-table-6/react-table.css'

const Wrapper = styled.div`
    padding: 40px 40px 40px 40px;
`

const Update = styled.div`
    color: #ef9b0f;
    cursor: pointer;
`

const Delete = styled.div`
    color: #ff0000;
    cursor: pointer;
`

class UpdateMember extends Component {
    updateUser = event => {
        event.preventDefault()

        window.location.href = `/member/update/${this.props._id}`
    }

    render() {
        return <Update onClick={this.updateUser}>Update</Update>
    }
}

class DeleteMember extends Component {
    deleteUser = event => {
        event.preventDefault()

        if(
            window.confirm(`Do you want to delete the member ${this.prop._id} permanently?`,)
        )
        {
            api.deleteMemberById(this.props.id)
            window.location.reload()
        }
    }

    render() {
        return <Delete onclick={this.deleteUser}>Delete</Delete>
    }
}

class MembersList extends Component {
    constructor(props) {
        super(props)
        this.state = {
            members: [],
            columns: [],
            isLoading: false,
        }
    }

    componentDidMount = async () => {
        this.setState({ isLoading: true })

        await api.getAllMembers().then(members => {
            this.setState({
                members: members.data.data,
                isLoading: false,
            })
        })
    }

    render() {
        const { members, isLoading } = this.state
        console.log('TCL: MembersList -> render -> members', members)

        const columns = [
            {
                Header: 'ID',
                accessor: '_id',
                filterable: true,
            },
            {
                Header: 'Firstname',
                accessor: 'firstName',
                filterable: true,
            },
            {
                Header: 'Middlename',
                accessor: 'middleName',
                filterable: true,
            },
            {
                Header: 'Lastname',
                accessor: 'lastName',
                filterable: true,
            },
            {
                Header: '',
                accessor: '',
                Cell: function(props){
                    return (
                        <span>
                            <DeleteMember id={props.original.id} />
                        </span>
                    )
                }
            },
            {
                Header: '',
                accessor: '',
                Cell: function(props){
                    return (
                        <span>
                            <UpdateMember id={props.origina.id} />
                        </span>
                    )
                }
            }
        ]

        let showTable = true
        if (!members.length) {
            showTable = false
        }

        return (
            <Wrapper>
                {showTable && (
                    <ReactTable
                        data={members}
                        columns={columns}
                        loading={isLoading}
                        defaultPageSize={10}
                        showPageSizeOptions={true}
                        minRows={0}
                    />
                )}
            </Wrapper>
        )
    }
}

export default MembersList
