import React, { Component } from 'react'
import ReactTable from 'react-table-6'
import api from '../../api'

import styled from 'styled-components'

import 'react-table-6/react-table.css'

const Wrapper = styled.div`
    padding:130px 50px 50px 50px; 
    position: relative;
`

const GetOrgMembers = styled.div`
    color: #ef9b0f;
    cursor: pointer;
`

class GetOrganizationMembers extends Component {
    getMemberByOrg = event => {
        event.preventDefault()

        window.location.href = `/member/getmembersbyorg/${this.props.id}`
    }

    render() {
        return <GetOrgMembers onClick={this.getMemberByOrg}>{this.props.id}</GetOrgMembers>
    }
}

class OrganizationsList extends Component {
    constructor(props) {
        super(props)
        this.state = {
            organizations: [],
            columns: [],
            isLoading: false,
        }
    }

    componentDidMount = async () => {
        this.setState({ isLoading: true })

        await api.getAllOrganizations().then(organizations => {
            this.setState({
                organizations: organizations.data.data,
                isLoading: false,
            })
        })
    }

    render() {
        const { organizations, isLoading } = this.state
        const columns = [
            // {
            //     Header: 'ID',
            //     accessor: '_id',
            //     filterable: true,
            // },
            {
                Header: 'Abbreviation',
                accessor: '',
                Cell: function(props){
                    return (
                        <span>
                            <GetOrganizationMembers id={props.original.abbreviation} />
                        </span>
                    )
                },
                headerStyle: {
                    },
                filterable: true,
                width: '30%'
            },            
            // {
            //     Header: 'Abbreviation',
            //     accessor: 'abbreviation',
            //     filterable: true,
            //     width: '30%'
            // },
            {
                Header: 'Name',
                accessor: 'name',
                filterable: true,
                width: '30%'
            },
            // {
            //     Header: 'ID',
            //     accessor: 'organizationId',
            //     filterable: true,
            //     width: '30%'
            // },
            // {
            //     Header: '',
            //     accessor: '',
            //     Cell: function(props){
            //         return (
            //             <span>
            //                 <UpdateMember id={props.original._id} />
            //             </span>
            //         )
            //     },
            //     headerStyle: {
            //         },
            //     width: '100',
            //     sortable:false
            // },
            // {
            //     Header: '',
            //     accessor: '',
            //     Cell: function(props){
            //         return (
            //             <span>
            //                 <DeleteMember id={props.original._id} />
            //             </span>
            //         )
            //     },
            //     headerStyle: {
            //         },
            //     width: '100',
            //     sortable:false
            // }
        ]

        let showTable = true
        if (!organizations.length) {
            showTable = false
        }

        return (
            <Wrapper>
                {showTable && (
                    <ReactTable
                        data={organizations}
                        columns={columns}
                        loading={isLoading}
                        defaultPageSize={50}
                        showPageSizeOptions={true}
                        minRows={0}
                        className="-highlight -striped"
                        getTdProps={() => ({
                            style: {
                            textAlign: 'center'
                            }
                            })}
                    />
                )}
            </Wrapper>
        )
    }
}

export default OrganizationsList
