import React, { Component } from 'react'
import ReactTable from 'react-table-6'
import api from '../api'

import styled from 'styled-components'

import 'react-table-6/react-table.css'

const Wrapper = styled.div`
    padding:100px;    
    position: relative;
    padding: 30px 30px 40px 40px;
`

const Update = styled.div`
    color: #ef9b0f;
    cursor: pointer;
`

const Delete = styled.div`
    color: #ff0000;
    cursor: pointer;
`

class UpdateMember extends Component {
    updateUser = event => {
        event.preventDefault()

        window.location.href = `/member/update/${this.props.id}`
    }

    render() {
        return <Update onClick={this.updateUser}>Update</Update>
    }
}

class DeleteMember extends Component {
    deleteUser = event => {
        event.preventDefault()

        if(
            window.confirm(`Do you want to delete the member ${this.props.id} permanently?`,)
        )
        {
            api.deleteMemberById(this.props.id)
            window.location.reload()
        }
    }

    render() {
        return <Delete onClick={this.deleteUser}>Delete</Delete>
    }
}

class MembersList extends Component {
    constructor(props) {
        super(props)
        this.state = {
            members: [],
            columns: [],
            isLoading: false,
        }
    }

    componentDidMount = async () => {
        this.setState({ isLoading: true })

        await api.getAllMembers().then(members => {
            this.setState({
                members: members.data.data,
                isLoading: false,
            })
        })
    }

    render() {
        const { members, isLoading } = this.state
        console.log('TCL: MembersList -> render -> members', members)

        const columns = [
            // {
            //     Header: 'ID',
            //     accessor: '_id',
            //     filterable: true,
            // },
            {
                Header: 'First Name',
                accessor: 'firstName',
                filterable: true,
                width: '30%'
            },
            {
                Header: 'Middle Name',
                accessor: 'middleName',
                filterable: true,
                width: '30%'
            },
            {
                Header: 'Last Name',
                accessor: 'lastName',
                filterable: true,
                width: '30%'
            },
            {
                Header: 'Birthday',
                accessor: 'birthday',
                filterable: true,
                width: '30%'
            },
            {
                Header: 'Date of Baptism',
                accessor: 'baptismDate',
                filterable: true,
                width: '30%'
            },
            {
                Header: 'Baptized By',
                accessor: 'baptizedBy',
                filterable: true,
                width: '30%'
            },
            {
                Header: 'Occupation',
                accessor: 'occupation',
                filterable: true,
                width: '30%'
            },
            {
                Header: '',
                accessor: '',
                Cell: function(props){
                    return (
                        <span>
                            <UpdateMember id={props.original._id} />
                        </span>
                    )
                },
                headerStyle: {
                    },
                width: '100'
            },
            {
                Header: '',
                accessor: '',
                Cell: function(props){
                    return (
                        <span>
                            <DeleteMember id={props.original._id} />
                        </span>
                    )
                },
                headerStyle: {
                    },
                width: '100'
            }
        ]

        let showTable = true
        if (!members.length) {
            showTable = false
        }

        return (
            <Wrapper>
                {showTable && (
                    <ReactTable
                        data={members}
                        columns={columns}
                        loading={isLoading}
                        defaultPageSize={20}
                        showPageSizeOptions={true}
                        minRows={0}
                        className="-highlight -striped"
                    />
                )}
            </Wrapper>
        )
    }
}

export default MembersList
