import React, { Component } from 'react'
import api from '../api'

import styled from 'styled-components'
import { Textbox, Radiobox, Checkbox, Select, Textarea } from 'react-inputs-validation'; 
import ModalPopup from './modal_popup';  
import 'react-inputs-validation/lib/react-inputs-validation.min.css';

import { EmailInput,Numeric,Letter,AlphaNumeric,CustomRx,Required } from 'react-js-validator'
//import DatePicker from "react-datepicker";
//import "react-datepicker/dist/react-datepicker.css";

const Title = styled.h1.attrs({
    className: 'h1',
})``

const Wrapper = styled.div.attrs({
    className: 'form-group'
})`
    padding:100px;
    position: relative;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
`

const InputWrapper = styled.div.attrs({
    className: 'form-group form-inline'
})`
    margin: 15px 30px;
`

const Label = styled.label`
    margin: 5px;
    width: 120px;
`

const InputText = styled.input.attrs({
    className: 'form-control',
})`
    margin-right: 5px;
    width: 600px;
    display: inline-block;
    border-radius: 10px!important;
    ::placeholder {
        color: grey;
        font-style:italic;
        font-size: small;
      };
`

const ButtonWrapper = styled.div.attrs({
    className: ``,
})`
    float: right;
`

const Button = styled.button.attrs({
    className: `btn btn-success`,
})`
    margin: 15px 15px 15px 5px;
`

const CancelButton = styled.a.attrs({
    className: `btn btn-danger`,
})`
    margin: 15px 15px 15px 5px;
`

class MembersInsert extends Component {
    constructor(props) {
        super(props)

        this.state = {
            firstName: '',
            middleName: '',
            lastName: '',
            birthday: '',
            occupation: '',
            baptismDate: '',
            baptizedBy: '',
            showModalPopup: false  
        }
    }

    isShowPopup = (status) => {  
        this.setState({ showModalPopup: status });  
      };  

    handleChangeInputFirstname = async event => {
        const firstName = event.target.value
        this.setState({ firstName })
    }

    handleChangeInputMiddlename = async event => {
        const middleName = event.target.value
        this.setState({ middleName })
    }

    handleChangeInputLastname = async event => {
        const lastName = event.target.value
        this.setState({ lastName })
    }

    handleChangeInputOccupation = async event => {
        const occupation = event.target.value
        this.setState({ occupation })
    }

    handleChangeInputBaptizedBy = async event => {
        const baptizedBy = event.target.value
        this.setState({ baptizedBy })
    }

    handleChangeInputBaptismDate = async event => {
        const baptismDate = event.target.value
        this.setState({ baptismDate })
    }

    handleChangeInputBirthday = async event => {
        const birthday = event.target.value
        this.setState({ birthday })
    }

    handleIncludeMember = async () => {
        const { firstName, middleName, lastName, birthday, occupation, baptismDate, baptizedBy } = this.state
        const payload = { firstName, middleName, lastName, birthday, occupation, baptismDate, baptizedBy  }

        await api.insertMember(payload).then(res => {
            //window.alert(`Member inserted successfully`)
            this.state.showModalPopup = true
            this.setState({
                firstName: '',
                middleName: '',
                lastName: '',
                birthday: '',
                occupation: '',
                baptismDate: '',
                baptizedBy: ''
            })
        })
    }

    render() {
        const { firstName, middleName, lastName, birthday, occupation, baptismDate, baptizedBy } = this.state
        return (
            <Wrapper>
                <Title>Create Member</Title>

                {/* <InputWrapper>
                    <Label>First Name: </Label>
                    <Textbox
                        attributesInput={{ // Optional.
                            id: 'Name',
                            name: 'Name',
                            type: 'text',
                            placeholder: 'Enter First Name',
                          }}
                          value={firstName} // Optional.[String].Default: "".
                          onChange={(firstName, e) => {
                            this.setState({ firstName });
                            console.log(e);
                          }} // Required.[Func].Default: () => {}. Will return the value.
                          onBlur={(e) => {console.log(e)}} // Optional.[Func].Default: none. In order to validate the value on blur, you MUST provide a function, even if it is an empty function. Missing this, the validation on blur will not work.
                          validationOption={{
                            name: 'Name', // Optional.[String].Default: "". To display in the Error message. i.e Please enter your {name}.
                            check: true, // Optional.[Bool].Default: true. To determin if you need to validate.
                            required: true // Optional.[Bool].Default: true. To determin if it is a required field.
                          }}
                          className="form-control"
                          style="margin-right: 5px;
                          width: 600px;
                          display: inline-block;
                          ::placeholder {
                              color: grey;
                              font-style:italic;
                              font-size: small;
                            };"
                    />
                </InputWrapper> */}
                

                <InputWrapper>
                    <Label>First Name: </Label>
                    <InputText
                        type="text"
                        value={firstName}
                        onChange={this.handleChangeInputFirstname}
                        placeholder="Enter First Name"
                    />
                </InputWrapper>

                <InputWrapper>
                <Label>Middle Name: </Label>
                    <InputText
                        type="text"
                        value={middleName}
                        onChange={this.handleChangeInputMiddlename}
                        placeholder="Enter Middle Name"
                    />
                </InputWrapper>

                <InputWrapper>
                <Label>Last Name: </Label>
                    <InputText
                        type="text"
                        value={lastName}
                        onChange={this.handleChangeInputLastname}
                        placeholder="Enter Last Name"
                    />
                </InputWrapper>

                {/* <Label>Birthday: </Label>
                <DatePicker 
                    placeholderText="Select Date"
                    selected={birthday}
                    onChange={birthday => setDate(birthday)}
                    showMonthDropdown
                    showYearDropdown
                    scrollableYearDropdown
                    yearDropdownItemNumber={120}
                    maxDate={new Date()}
                    className="form-group"
                    width="500px"
                    margin="5px"
                /> */}

                <InputWrapper>
                <Label>Birthday: </Label>
                    <InputText
                        type="text"
                        value={birthday}
                        onChange={this.handleChangeInputBirthday}
                        placeholder="Enter Birthday"
                    />
                </InputWrapper>

                <InputWrapper>
                <Label>Date of Baptism: </Label>
                    <InputText
                        type="text"
                        value={baptismDate}
                        onChange={this.handleChangeInputBaptismDate}
                        placeholder="Enter Date of Baptism"
                    />
                </InputWrapper>

                <InputWrapper>
                <Label>Baptized By: </Label>
                    <InputText
                        type="text"
                        value={baptizedBy}
                        onChange={this.handleChangeInputBaptizedBy}
                        placeholder="Enter Baptized By"
                    />
                </InputWrapper>

                <InputWrapper>
                    <Label>Occupation: </Label>
                    <InputText
                        type="text"
                        value={occupation}
                        onChange={this.handleChangeInputOccupation}
                        placeholder="Enter Occupation"
                    />
                </InputWrapper>
                <ButtonWrapper>
                    <Button onClick={this.handleIncludeMember}>Add Member</Button>
                    <CancelButton href={'/members/list'}>Cancel</CancelButton>
                </ButtonWrapper>
                <ModalPopup  
                    showModalPopup={this.state.showModalPopup}  
                    onPopupClose={this.isShowPopup}  
                ></ModalPopup>
                
                <EmailInput max={10} min={3} />
            </Wrapper>
        )
    }
}

export default MembersInsert
