import React, { Component } from 'react'
import ReactTable from 'react-table-6'
import api from '../../api'

import styled from 'styled-components'

import 'react-table-6/react-table.css'

const Wrapper = styled.div`
    padding:100px;    
    position: relative;
`

class OrganizationsList extends Component {
    constructor(props) {
        super(props)
        this.state = {
            organizations: [],
            columns: [],
            isLoading: false,
        }
    }

    componentDidMount = async () => {
        this.setState({ isLoading: true })

        await api.getAllOrganizations().then(organizations => {
            this.setState({
                organizations: organizations.data.data,
                isLoading: false,
            })
        })
    }

    render() {
        const { organizations, isLoading } = this.state
        const columns = [
            // {
            //     Header: 'ID',
            //     accessor: '_id',
            //     filterable: true,
            // },            
            {
                Header: 'Abbreviation',
                accessor: 'abbreviation',
                filterable: true,
                width: '30%',
                cell: cellContent => <CenterAlign>{cellContent}</CenterAlign>
            },
            {
                Header: 'Name',
                accessor: 'name',
                filterable: true,
                width: '30%'
            },
            // {
            //     Header: 'ID',
            //     accessor: 'organizationId',
            //     filterable: true,
            //     width: '30%'
            // },
            // {
            //     Header: '',
            //     accessor: '',
            //     Cell: function(props){
            //         return (
            //             <span>
            //                 <UpdateMember id={props.original._id} />
            //             </span>
            //         )
            //     },
            //     headerStyle: {
            //         },
            //     width: '100',
            //     sortable:false
            // },
            // {
            //     Header: '',
            //     accessor: '',
            //     Cell: function(props){
            //         return (
            //             <span>
            //                 <DeleteMember id={props.original._id} />
            //             </span>
            //         )
            //     },
            //     headerStyle: {
            //         },
            //     width: '100',
            //     sortable:false
            // }
        ]

        let showTable = true
        if (!organizations.length) {
            showTable = false
        }

        return (
            <Wrapper>
                {showTable && (
                    <ReactTable
                        data={organizations}
                        columns={columns}
                        loading={isLoading}
                        defaultPageSize={50}
                        showPageSizeOptions={true}
                        minRows={0}
                        className="-highlight -striped"
                    />
                )}
            </Wrapper>
        )
    }
}

export default OrganizationsList
